print(f"üì¶ File size: {len(image_data)} bytes")

# Kaydet
output_path = "nfc_photo_decoded.jpg"
image.save(output_path)
print(f"\n‚úÖ Saved to: {output_path}")

# Detaylƒ± analiz
print(f"\nüîç ANALYSIS:")
